glsl4.vert:
precision highp float;
layout(set = 0, binding = 0) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
  mediump vec4 cc_probeInfo;
  mediump vec4 cc_debug_view_mode;
};
layout(set = 0, binding = 1) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_surfaceTransform;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
  mediump vec4 cc_nearFar;
  mediump vec4 cc_viewPort;
};
layout(set = 1, binding = 0) uniform WaterEffectVS {
    highp float waveAmplitude;
    highp float waveFrequency;
    highp float waveSpeed;
    highp float edgeDamping;
    highp float time;
    highp float randomSeed;
};
layout(location = 0) in vec3 a_position;
layout(location = 1) in vec2 a_texCoord;
layout(location = 2) in vec4 a_color;
layout(location = 0) out vec4 v_color;
layout(location = 1) out vec2 v_texCoord;
layout(location = 2) out float v_waveAmplitude;
float calcY() {
    float centerX = (a_texCoord.x - 0.5) * 2.0;
    float edgeFactor = 1.0 - pow(abs(centerX), edgeDamping);
    float upperMask = smoothstep(0.45, 0.55, a_texCoord.y);
    float lowerMask = 1.0 - upperMask;
    float wave = sin(a_texCoord.y * waveFrequency + time * waveSpeed) *
               cos((a_texCoord.x + randomSeed) * waveFrequency * 0.5) * waveAmplitude;
    float upperDeform = wave * upperMask * edgeFactor;
    upperDeform = max(0.0, upperDeform);
    float lowerDeform = wave * lowerMask * edgeFactor;
    return  (upperDeform + lowerDeform) * 2.0;
}
vec4 vert() {
    vec4 pos = vec4(a_position, 1.0);
    if (waveAmplitude > 0.1) {
        pos.y += calcY();
    }
    gl_Position = cc_matViewProj * pos;
    v_texCoord = a_texCoord;
    v_color = a_color;
    return gl_Position;
}
void main() { gl_Position = vert(); }

glsl4.frag:
precision highp float;
layout(location = 0) in vec4 v_color;
layout(location = 1) in vec2 v_texCoord;
layout(set = 1, binding = 1) uniform sampler2D mainTexture;
vec4 frag() {
    vec4 texColor = texture(mainTexture, v_texCoord);
    vec4 finalColor = v_color;
    finalColor.a = texColor.a;
    return finalColor;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

